package com.SYUcap.SYUcap.User;

import org.springframework.security.core.GrantedAuthority;
import org.springframework.security.core.userdetails.User;

import java.util.List;

public class CustomUser extends User {
    public Long id;
    public String displayName;

    public Long getId() {
        return this.id;
    }

    public CustomUser(String username,
                      String password,
                      List<GrantedAuthority> authorities ) {
        super(username, password, authorities);
    }
}

package com.SYUcap.SYUcap.User;

import org.springframework.security.core.GrantedAuthority;
import org.springframework.security.core.userdetails.User;

import java.util.Collection;

public class CustomUser extends User {
    private final Long id;
    private final String userName;

    public CustomUser(String username,
                      String password,
                      Collection<? extends GrantedAuthority> authorities,
                      Long id,
                      String userName) {
        super(username, password, authorities);
        this.id = id;
        this.userName = userName;
    }

    public Long getId() {
        return this.id;
    }

    public String getUserName() {
        return this.userName;
    }
}

package com.SYUcap.SYUcap.User;

import lombok.RequiredArgsConstructor;
import org.springframework.security.core.GrantedAuthority;
import org.springframework.security.core.authority.SimpleGrantedAuthority;
import org.springframework.security.core.userdetails.UserDetails;
import org.springframework.security.core.userdetails.UserDetailsService;
import org.springframework.security.core.userdetails.UsernameNotFoundException;
import org.springframework.stereotype.Service;

import java.util.ArrayList;
import java.util.List;

@RequiredArgsConstructor
@Service
public class MyUserDetailsService implements UserDetailsService {
    private final UserRepository userRepository;
    @Override
    public UserDetails loadUserByUsername(String userId) throws UsernameNotFoundException {
        var result = userRepository.findByUserId(userId);
        if (result.isEmpty()){
            throw new UsernameNotFoundException("해당 아이디 없음");
        }
        var user = result.get();
        List<GrantedAuthority> authorities = new ArrayList<>();
        authorities.add(new SimpleGrantedAuthority("ROLE_USER"));
        var a =  new CustomUser(user.getUserId(), user.getPassword(), authorities);
        a.id = user.getId();
        a.displayName = user.getUserName();
        return a;
    }

}

package com.SYUcap.SYUcap.User;

import lombok.RequiredArgsConstructor;
import org.springframework.security.core.GrantedAuthority;
import org.springframework.security.core.authority.SimpleGrantedAuthority;
import org.springframework.security.core.userdetails.UserDetails;
import org.springframework.security.core.userdetails.UserDetailsService;
import org.springframework.security.core.userdetails.UsernameNotFoundException;
import org.springframework.stereotype.Service;

import java.util.ArrayList;
import java.util.List;

@RequiredArgsConstructor
@Service
public class MyUserDetailsService implements UserDetailsService {
    private final UserRepository userRepository;
    @Override
    public UserDetails loadUserByUsername(String userId) throws UsernameNotFoundException {
        var result = userRepository.findByUserId(userId);
        if (result.isEmpty()){
            throw new UsernameNotFoundException("해당 아이디 없음");
        }
        var user = result.get();
        List<GrantedAuthority> authorities = new ArrayList<>();
        authorities.add(new SimpleGrantedAuthority("ROLE_USER"));
        return new CustomUser(user.getUserId(), user.getPassword(), authorities, user.getId(), user.getUserName());
    }

}

package com.SYUcap.SYUcap.User;

import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.security.config.annotation.web.builders.HttpSecurity;
import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;
import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;
import org.springframework.security.crypto.password.PasswordEncoder;
import org.springframework.security.web.SecurityFilterChain;

@Configuration
@EnableWebSecurity
public class SecurityConfig {
    @Bean
    PasswordEncoder passwordEncoder() {
        return new BCryptPasswordEncoder();
    }
    @Bean
    public SecurityFilterChain filterChain(HttpSecurity http) throws Exception {
        http.csrf((csrf) -> csrf.disable());
        http.authorizeHttpRequests((authorize) ->
                authorize.requestMatchers("/**").permitAll()
        );
        http.formLogin((formLogin) -> formLogin.loginPage("/login")
                .defaultSuccessUrl("/home")
                .failureUrl("/fail")
                .usernameParameter("userId")
        );
        return http.build();
    }
}

package com.SYUcap.SYUcap.User;

import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.security.config.annotation.web.builders.HttpSecurity;
import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;
import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;
import org.springframework.security.crypto.password.PasswordEncoder;
import org.springframework.security.web.SecurityFilterChain;

@Configuration
@EnableWebSecurity
public class SecurityConfig {
    @Bean
    PasswordEncoder passwordEncoder() {
        return new BCryptPasswordEncoder();
    }
    @Bean
    public SecurityFilterChain filterChain(HttpSecurity http) throws Exception {
        http.csrf((csrf) -> csrf.disable());
        http.authorizeHttpRequests((authorize) -> authorize
                .requestMatchers("/board/**").authenticated()
                .anyRequest().permitAll()
        );
        http.formLogin((formLogin) -> formLogin.loginPage("/login")
                .defaultSuccessUrl("/home")
                .failureUrl("/login?error")
                .usernameParameter("userId")
        );
        return http.build();
    }
}

package com.SYUcap.SYUcap.User;


import lombok.RequiredArgsConstructor;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestParam;

@Controller
@RequiredArgsConstructor
public class UserController {
    private final UserService userService;
    private final UserRepository userRepository;

    /** 회원가입 폼 */
    @GetMapping("/signup")
    String signup() {
        return "signup.html";
    }

    @GetMapping("/login")
    String login(@RequestParam(value = "error", required = false) String error, Model model) {
        if (error != null) {
            model.addAttribute("error", true);
        }
        return "login";
    }
    /** 회원가입 */
    @PostMapping("/signup")
    String addUser(@RequestParam String userId, @RequestParam String password,
                          @RequestParam String userName ) {
        userService.addUser(userId, password, userName);
        return "login.html";
    }
}

package com.SYUcap.SYUcap.User;


import lombok.RequiredArgsConstructor;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestParam;

@Controller
@RequiredArgsConstructor
public class UserController {
    private final UserService userService;
    private final UserRepository userRepository;

    /** 회원가입 폼 */
    @GetMapping("/signup")
    String signup() {
        return "signup.html";
    }

    @GetMapping("/login")
    String login(@RequestParam(value = "error", required = false) String error, Model model) {
        if (error != null) {
            model.addAttribute("error", true);
        }
        return "login";
    }
    /** 회원가입 */
    @PostMapping("/signup")
    String addUser(@RequestParam String userId,
                   @RequestParam String password,
                   @RequestParam String passwordConfirm,
                   @RequestParam String userName ) {
        userService.addUser(userId, password, passwordConfirm, userName);
        return "login.html";
    }
}

package com.SYUcap.SYUcap.User;

import lombok.RequiredArgsConstructor;
import org.springframework.security.crypto.password.PasswordEncoder;
import org.springframework.stereotype.Service;

@Service
@RequiredArgsConstructor
public class UserService {
    private final UserRepository userRepository;
    private final PasswordEncoder passwordEncoder;

    public void addUser(String userId, String password, String username) {
        // 아이디 중복 검사
        if (userRepository.findByUserId(userId).isPresent()) {
            throw new IllegalArgumentException("이미 존재하는 아이디입니다.");
        }
        // 비밀번호 유효성 검사: 6~16자, 특수문자 1개 이상
        if (!isValidPassword(password)) {
            throw new IllegalArgumentException("비밀번호는 6~16자이며 특수문자를 최소 1개 포함해야 합니다.");
        }
        Users user = new Users();
        var hash = passwordEncoder.encode(password);
        user.setUserId(userId);
        user.setPassword(hash);
        user.setUserName(username);
        userRepository.save(user);
    }

    private boolean isValidPassword(String password) {
        if (password == null) return false;
        int len = password.length();
        if (len < 6 || len > 16) return false;
        // 숫자/영문이 아닌 문자(특수문자) 포함 여부
        return password.matches(".*[^a-zA-Z0-9].*");
    }
}

package com.SYUcap.SYUcap.User;

import lombok.RequiredArgsConstructor;
import org.springframework.security.crypto.password.PasswordEncoder;
import org.springframework.stereotype.Service;

@Service
@RequiredArgsConstructor
public class UserService {
    private final UserRepository userRepository;
    private final PasswordEncoder passwordEncoder;

    public void addUser(String userId, String password, String passwordConfirm, String username) {
        // 아이디 중복 검사
        if (userRepository.findByUserId(userId).isPresent()) {
            throw new IllegalArgumentException("이미 존재하는 아이디입니다.");
        }
        // 비밀번호 유효성 검사: 6~16자, 특수문자 1개 이상
        if (!isValidPassword(password)) {
            throw new IllegalArgumentException("비밀번호는 6~16자이며 특수문자를 최소 1개 포함해야 합니다.");
        }
        // 비밀번호 확인 일치 검사
        if (passwordConfirm == null || !password.equals(passwordConfirm)) {
            throw new IllegalArgumentException("비밀번호와 비밀번호 확인이 일치하지 않습니다.");
        }
        Users user = new Users();
        var hash = passwordEncoder.encode(password);
        user.setUserId(userId);
        user.setPassword(hash);
        user.setUserName(username);
        userRepository.save(user);
    }

    private boolean isValidPassword(String password) {
        if (password == null) return false;
        int len = password.length();
        if (len < 6 || len > 16) return false;
        // 숫자/영문이 아닌 문자(특수문자) 포함 여부
        return password.matches(".*[^a-zA-Z0-9].*");
    }
}

