package com.SYUcap.SYUcap.Board;

import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;

import java.util.List;

public interface BoardRepository extends JpaRepository<Board,Long> {
    List<Board> findByTitleContainingOrContentContaining(String titleKeyword, String contentKeyword);

    List<Board> findAllByOrderByCreatedAtDesc();
    @Query("select b from Board b join fetch b.user u order by b.createdAt desc")
    java.util.List<Board> findAllWithUserFetchJoinOrderByCreatedAtDesc();

    @Query("select b from Board b join fetch b.user u where b.category = :cat order by b.createdAt desc")
    java.util.List<Board> findByCategoryWithUserFetchJoinOrderByCreatedAtDesc(@Param("cat") String category);

    @Query("select b from Board b join fetch b.user u where b.title like concat('%', :keyword, '%') or b.content like concat('%', :keyword, '%')")
    java.util.List<Board> findByKeywordWithUserFetchJoin(@Param("keyword") String keyword);
}

package com.SYUcap.SYUcap.Board;

import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;

import java.util.List;

public interface BoardRepository extends JpaRepository<Board,Long> {
    List<Board> findByTitleContainingOrContentContaining(String titleKeyword, String contentKeyword);

    List<Board> findAllByOrderByCreatedAtDesc();
    @Query("select b from Board b left join fetch b.user u order by b.createdAt desc")
    java.util.List<Board> findAllWithUserFetchJoinOrderByCreatedAtDesc();

    @Query("select b from Board b left join fetch b.user u where b.category = :cat order by b.createdAt desc")
    java.util.List<Board> findByCategoryWithUserFetchJoinOrderByCreatedAtDesc(@Param("cat") String category);

    @Query("select b from Board b left join fetch b.user u where b.title like concat('%', :keyword, '%') or b.content like concat('%', :keyword, '%')")
    java.util.List<Board> findByKeywordWithUserFetchJoin(@Param("keyword") String keyword);
}

package com.SYUcap.SYUcap.Board;

import lombok.RequiredArgsConstructor;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import java.util.List;
import java.util.Objects;

@Service
@RequiredArgsConstructor
@Transactional
public class BoardService {

    private final BoardRepository boardRepository;

    // 제목은 20자 이하
    public static boolean isTitleValid(String title) {
        return title != null && title.length() <= 20;
    }

    // 내용은 200자 이하
    public static boolean isContentValid(String content) {
        return content != null && content.length() <= 200;
    }

    // 시작 시간이 종료 시간 이후면 경고 메시지 반환, 아니면 null
    public static String validateMeetingTime(java.time.LocalDateTime start, java.time.LocalDateTime end) {
        if (start != null && end != null && start.isAfter(end)) {
            return "시작 시간이 종료 시간보다 빠를 수 없습니다.";
        }
        return null;
    }

    // 제한 인원이 1 미만이면 경고 메시지 반환, 아니면 null
    public static String validateLimitCount(Integer limitCount) {
        if (limitCount == null || limitCount < 1) {
            return "최소 1명 이상이어야 합니다";
        }
        return null;
    }

    public List<Board> findAll() {
        return boardRepository.findAll();
    }

    public Board findById(Long id) {
        return boardRepository.findById(id)
                .orElseThrow(() -> new IllegalArgumentException("게시글을 찾을 수 없습니다: " + id));
    }

    public Board save(Board board) {
        Objects.requireNonNull(board, "게시글 정보가 null 입니다");
        if (!isTitleValid(board.getTitle())) {
            throw new IllegalArgumentException("제목 길이 20자를 초과할 수 없습니다");
        }
        if (!isContentValid(board.getContent())) {
            throw new IllegalArgumentException("내용 길이 200자를 초과할 수 없습니다");
        }
        String timeMsg = validateMeetingTime(board.getMeetingStartTime(), board.getMeetingEndTime());
        if (timeMsg != null) {
            throw new IllegalArgumentException(timeMsg);
        }
        String countMsg = validateLimitCount(board.getLimitCount());
        if (countMsg != null) {
            throw new IllegalArgumentException(countMsg);
        }
        return boardRepository.save(board);
    }

    public void delete(Long id) {
        boardRepository.deleteById(id);
    }

    public List<Board> searchBoards(String keyword) {
        if (keyword == null || keyword.trim().isEmpty()) {
            return boardRepository.findAll();
        }
        return boardRepository.findByTitleContainingOrContentContaining(keyword, keyword);
    }

    //전체 게시글을 생성일 기준 내림차순으로 정렬해서 반환
    @Transactional(readOnly = true)
    public List<Board> getAllSorted() {
        return boardRepository.findAllByOrderByCreatedAtDesc();
    }
}

package com.SYUcap.SYUcap.Board;

import lombok.RequiredArgsConstructor;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import java.util.List;
import java.util.Objects;

@Service
@RequiredArgsConstructor
@Transactional
public class BoardService {

    private final BoardRepository boardRepository;

    // 제목은 20자 이하
    public static boolean isTitleValid(String title) {
        return title != null && title.length() <= 20;
    }

    // 내용은 200자 이하
    public static boolean isContentValid(String content) {
        return content != null && content.length() <= 200;
    }

    // 시작 시간이 종료 시간 이후면 경고 메시지 반환, 아니면 null
    public static String validateMeetingTime(java.time.LocalDateTime start, java.time.LocalDateTime end) {
        if (start != null && end != null && start.isAfter(end)) {
            return "시작 시간이 종료 시간보다 빠를 수 없습니다.";
        }
        return null;
    }

    // 제한 인원이 1 미만이면 경고 메시지 반환, 아니면 null
    public static String validateLimitCount(Integer limitCount) {
        if (limitCount == null || limitCount < 1) {
            return "최소 1명 이상이어야 합니다";
        }
        return null;
    }

    public List<Board> findAll() {
        return boardRepository.findAllWithUserFetchJoinOrderByCreatedAtDesc();
    }

    public Board findById(Long id) {
        return boardRepository.findById(id)
                .orElseThrow(() -> new IllegalArgumentException("게시글을 찾을 수 없습니다: " + id));
    }

    public Board save(Board board) {
        Objects.requireNonNull(board, "게시글 정보가 null 입니다");
        if (!isTitleValid(board.getTitle())) {
            throw new IllegalArgumentException("제목 길이 20자를 초과할 수 없습니다");
        }
        if (!isContentValid(board.getContent())) {
            throw new IllegalArgumentException("내용 길이 200자를 초과할 수 없습니다");
        }
        String timeMsg = validateMeetingTime(board.getMeetingStartTime(), board.getMeetingEndTime());
        if (timeMsg != null) {
            throw new IllegalArgumentException(timeMsg);
        }
        String countMsg = validateLimitCount(board.getLimitCount());
        if (countMsg != null) {
            throw new IllegalArgumentException(countMsg);
        }
        return boardRepository.save(board);
    }

    public void delete(Long id) {
        boardRepository.deleteById(id);
    }

    public List<Board> searchBoards(String keyword) {
        if (keyword == null || keyword.trim().isEmpty()) {
            return boardRepository.findAllWithUserFetchJoinOrderByCreatedAtDesc();
        }
        return boardRepository.findByKeywordWithUserFetchJoin(keyword);
    }

    //전체 게시글을 생성일 기준 내림차순으로 정렬해서 반환
    @Transactional(readOnly = true)
    public List<Board> getAllSorted() {
        return boardRepository.findAllWithUserFetchJoinOrderByCreatedAtDesc();
    }

    // 카테고리별 목록 페치 조인
    @Transactional(readOnly = true)
    public List<Board> getByCategorySorted(String category) {
        return boardRepository.findByCategoryWithUserFetchJoinOrderByCreatedAtDesc(category);
    }
}

package com.SYUcap.SYUcap.Comment;

import com.SYUcap.SYUcap.Board.Board;
import jakarta.persistence.*;
import lombok.Getter;
import lombok.Setter;
import java.time.LocalDateTime;

@Entity
@Getter
@Setter
public class Comment {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id; // 댓글 ID

    @Column(columnDefinition = "TEXT")
    private String content; // 댓글 내용

    private String authorName; // 댓글 작성자 이름 (Users 엔티티와 직접 연결 X)

    private LocalDateTime createdAt; // 댓글 작성 시간

    // '하나의' 게시글(Board)에 '많은' 댓글(Comment)이 달릴 수 있음
    @ManyToOne
    private Board board; // 이 댓글이 속한 게시글

    @PrePersist
    protected void onCreate() {
        this.createdAt = LocalDateTime.now();
    }
}

package com.SYUcap.SYUcap.Comment;

import com.SYUcap.SYUcap.Board.Board;
import com.SYUcap.SYUcap.User.Users;
import jakarta.persistence.*;
import lombok.Getter;
import lombok.Setter;
import java.time.LocalDateTime;

@Entity
@Getter
@Setter
public class Comment {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id; // 댓글 ID

    @Column(columnDefinition = "TEXT")
    private String content; // 댓글 내용

    private String authorName; // 댓글 작성자 이름 (Users 엔티티와 직접 연결 X)

    private LocalDateTime createdAt; // 댓글 작성 시간

    // '하나의' 게시글(Board)에 '많은' 댓글(Comment)이 달릴 수 있음
    @ManyToOne
    private Board board; // 이 댓글이 속한 게시글

    // 작성자(로그인 유저) 연관관계
    @ManyToOne(fetch = FetchType.LAZY)
    private Users user;

    @PrePersist
    protected void onCreate() {
        this.createdAt = LocalDateTime.now();
    }
}

package com.SYUcap.SYUcap.Comment;

import com.SYUcap.SYUcap.User.CustomUser;
import lombok.RequiredArgsConstructor;
import org.springframework.security.core.Authentication;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.*;

import java.net.URLEncoder;
import java.nio.charset.StandardCharsets;

@Controller
@RequiredArgsConstructor
@RequestMapping("/board")
public class CommentController {

    private final CommentService commentService;

    @PostMapping("/{cat}/{id}/comment")
    public String addComment(@PathVariable("cat") String category,
                             @PathVariable("id") Long boardId,
                             @RequestParam("content") String content,
                             Authentication auth) {
        String authorName = "익명";
        if (auth != null && auth.getPrincipal() instanceof CustomUser cu) {
            authorName = cu.getUserName();
        }
        commentService.createComment(boardId, content, authorName);
        String encodedCat = URLEncoder.encode(category, StandardCharsets.UTF_8);
        return "redirect:/board/" + encodedCat + "/" + boardId;
    }
}

package com.SYUcap.SYUcap.Comment;

import com.SYUcap.SYUcap.User.CustomUser;
import com.SYUcap.SYUcap.User.Users;
import lombok.RequiredArgsConstructor;
import org.springframework.security.core.Authentication;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.*;

import java.net.URLEncoder;
import java.nio.charset.StandardCharsets;

@Controller
@RequiredArgsConstructor
@RequestMapping("/board")
public class CommentController {

    private final CommentService commentService;

    @PostMapping("/{cat}/{id}/comment")
    public String addComment(@PathVariable("cat") String category,
                             @PathVariable("id") Long boardId,
                             @RequestParam("content") String content,
                             Authentication auth) {
        String authorName = "익명";
        Users loginUser = null;
        if (auth != null && auth.getPrincipal() instanceof CustomUser cu) {
            authorName = cu.getUserName();
            loginUser = new Users();
            loginUser.setId(cu.getId());
            loginUser.setUserName(cu.getUserName());
            // 주어진 정보로 Users 프록시 객체를 구성 (필요 최소 필드)
        }
        commentService.createComment(boardId, content, authorName, loginUser);
        String encodedCat = URLEncoder.encode(category, StandardCharsets.UTF_8);
        return "redirect:/board/" + encodedCat + "/" + boardId;
    }
}

