package com.SYUcap.SYUcap.Comment;

import com.SYUcap.SYUcap.Board.Board;
import com.SYUcap.SYUcap.Board.BoardRepository;
import lombok.RequiredArgsConstructor;
import org.springframework.dao.DataIntegrityViolationException;
import org.springframework.stereotype.Service;
import java.util.List;

@Service
@RequiredArgsConstructor
public class CommentService {

    private final CommentRepository commentRepository;
    private final BoardRepository boardRepository; // BoardService가 아닌 Repository가 필요합니다.

    public void createComment(Long boardId, String content, String authorName) {
        // 유효성: boardId null 금지
        if (boardId == null) {
            throw new IllegalArgumentException("boardId가 null입니다");
        }
        // 유효성: content null 금지(정책상 DB 제약 위반으로 간주)
        if (content == null) {
            throw new DataIntegrityViolationException("content가 null입니다");
        }
        // 유효성: authorName null 금지(정책상 DB 제약 위반으로 간주)
        if (authorName == null) {
            throw new DataIntegrityViolationException("authorName이 null입니다");
        }

        Board board = boardRepository.findById(boardId)
                .orElseThrow(() -> new IllegalArgumentException("해당 게시글이 없습니다. id=" + boardId));

        Comment comment = new Comment();
        comment.setContent(content);
        comment.setAuthorName(authorName);
        comment.setBoard(board); // 조회한 Board 엔티티를 Comment에 연결

        commentRepository.save(comment);
    }


    public List<Comment> getCommentsByBoardId(Long boardId) {
        return commentRepository.findByBoardId(boardId);
    }
}

package com.SYUcap.SYUcap.Comment;

import com.SYUcap.SYUcap.Board.Board;
import com.SYUcap.SYUcap.Board.BoardRepository;
import com.SYUcap.SYUcap.User.Users;
import lombok.RequiredArgsConstructor;
import org.springframework.dao.DataIntegrityViolationException;
import org.springframework.stereotype.Service;
import java.util.List;

@Service
@RequiredArgsConstructor
public class CommentService {

    private final CommentRepository commentRepository;
    private final BoardRepository boardRepository;

    /**
     * 댓글 생성 (로그인 유저 연동)
     */
    public void createComment(Long boardId, String content, String authorName, Users user) {
        // 유효성: boardId null 금지
        if (boardId == null) {
            throw new IllegalArgumentException("boardId가 null입니다");
        }
        // 유효성: content null 금지(정책상 DB 제약 위반으로 간주)
        if (content == null) {
            throw new DataIntegrityViolationException("content가 null입니다");
        }
        // 유효성: authorName, user null 금지(둘 다 없으면 누가 작성했는지 알 수 없음)
        if (authorName == null && user == null) {
            throw new DataIntegrityViolationException("author 정보가 없습니다");
        }

        Board board = boardRepository.findById(boardId)
                .orElseThrow(() -> new IllegalArgumentException("해당 게시글이 없습니다. id=" + boardId));

        Comment comment = new Comment();
        comment.setContent(content);
        // authorName은 표시용, 로그인 유저가 있으면 우선 사용
        if (user != null && user.getUserName() != null) {
            comment.setAuthorName(user.getUserName());
        } else {
            comment.setAuthorName(authorName);
        }
        comment.setBoard(board);
        comment.setUser(user); // 댓글 작성자 정보 설정

        commentRepository.save(comment);
    }


    public List<Comment> getCommentsByBoardId(Long boardId) {
        return commentRepository.findByBoardId(boardId);
    }
}

package com.SYUcap.SYUcap;

import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.ControllerAdvice;
import org.springframework.web.bind.annotation.ExceptionHandler;

@ControllerAdvice // 모든 @Controller에 대해 전역적으로 예외를 처리
public class GlobalExceptionHandler {

    // IllegalArgumentException이 발생하면 이 메서드가 대신 처리
    @ExceptionHandler(IllegalArgumentException.class)
    public ResponseEntity<String> handleIllegalArgument(IllegalArgumentException ex) {
        // 400 (Bad Request) 상태 코드와 예외 메시지를 본문에 담아 반환
        return ResponseEntity.status(HttpStatus.BAD_REQUEST).body(ex.getMessage());
    }
}

package com.SYUcap.SYUcap;

import org.springframework.dao.DataIntegrityViolationException;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.ControllerAdvice;
import org.springframework.web.bind.annotation.ExceptionHandler;

@ControllerAdvice // 모든 @Controller에 대해 전역적으로 예외를 처리
public class GlobalExceptionHandler {

    // IllegalArgumentException이 발생하면 이 메서드가 대신 처리
    @ExceptionHandler(IllegalArgumentException.class)
    public ResponseEntity<String> handleIllegalArgument(IllegalArgumentException ex) {
        // 400 (Bad Request) 상태 코드와 예외 메시지를 본문에 담아 반환
        return ResponseEntity.status(HttpStatus.BAD_REQUEST).body(ex.getMessage());
    }

    // DataIntegrityViolationException이 발생하면 이 메서드가 대신 처리
    @ExceptionHandler(DataIntegrityViolationException.class)
    public ResponseEntity<String> handleDataIntegrity(DataIntegrityViolationException ex) {
        // 400 (Bad Request) 상태 코드와 사용자 친화적인 메시지를 본문에 담아 반환
        return ResponseEntity.status(HttpStatus.BAD_REQUEST).body("이미 존재하는 아이디입니다.");
    }
}

package com.SYUcap.SYUcap;

import com.SYUcap.SYUcap.Board.Board;
import com.SYUcap.SYUcap.Board.BoardService;
import lombok.RequiredArgsConstructor;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;

import java.util.Comparator;
import java.util.List;

@Controller
@RequiredArgsConstructor
public class HomeController {

    private final BoardService boardService;

    @GetMapping({"/", "/home"})
    public String home(Model model) {
        model.addAttribute("active", "home");
        model.addAttribute("categories", new String[]{"게임", "스터디", "영화", "운동", "밥약"});

        // 최근 글 10개 (createdAt 내림차순)
        List<Board> recentPosts = boardService.getAllSorted().stream()
                .sorted(Comparator.comparing(Board::getCreatedAt, Comparator.nullsLast(Comparator.naturalOrder())).reversed())
                .limit(10)
                .toList();

        model.addAttribute("recentPosts", recentPosts);
        return "home";
    }
    @GetMapping("/notifications")
    public String notifications(Model model) {
        model.addAttribute("active", "notifications");
        return "notifications";
    }

    @GetMapping("/mypage")
    public String mypage(Model model) {
        return "mypage";
    }
}

