    @DisplayName("[B-TC-007] [실패] 내용 누락 시 등록 실패(DB 저장 안 됨)")
    void postBoardWrite_Fail_ContentMissing() throws Exception {
        // Given: 현재 게시글 수
        long before = boardRepository.count();

        // When: 내용 누락으로 등록 시도
        // Then: 폼 렌더링 및 DB 변경 없음 확인
        mockMvc.perform(post("/board/게임/write")
                        .param("title", "테스트 제목")
                        .param("content", "")
                        .param("authorName", "홍길동"))
                .andExpect(status().isOk())
                .andExpect(view().name("board-form"))
                .andExpect(content().string(org.hamcrest.Matchers.containsString("내용을 입력하���요")));

        long after = boardRepository.count();
        assertThat(after).isEqualTo(before);
    }

    @Test
    @DisplayName("[B-TC-008] [성공] 게시글 상세 보기")
    void getBoardDetail_Success() throws Exception {
        // Given: 게시글 1건 저장
        Board b = new Board();
        b.setCategory("게임");
        b.setTitle("상세 제목");
        b.setContent("상세 내용");
        b.setAuthorName("홍길동");
        b = boardRepository.save(b);

        // When/Then: 상세 페이지 접근 및 렌더링 확인
        mockMvc.perform(get("/board/게임/" + b.getId()))
                .andExpect(status().isOk())
                .andExpect(view().name("board-detail"))
                .andExpect(model().attribute("category", "게임"))
                .andExpect(model().attributeExists("post"))
                .andExpect(content().string(org.hamcrest.Matchers.containsString("상세 제목")));
    }

    @Test
    @DisplayName("[B-TC-009] [성공] 게시글 수정")
    void postBoardEdit_Success() throws Exception {
        // Given: 기존 게시글 저장
        Board b = new Board();
        b.setCategory("게임");
        b.setTitle("원래 제목");
        b.setContent("원래 내용");
        b.setAuthorName("홍길동");
        b = boardRepository.save(b);

        // When: 수정 요청
        // Then: 리다이렉트 및 DB 반영 확인
        mockMvc.perform(post("/board/게임/" + b.getId() + "/edit")
                        .param("title", "수정된 제목")
                        .param("content", "수정된 내용"))
                .andExpect(status().is3xxRedirection())
                .andExpect(redirectedUrl("/board/%EA%B2%8C%EC%9E%84/" + b.getId()));

        Board updated = boardRepository.findById(b.getId()).orElseThrow();
        assertThat(updated.getTitle()).isEqualTo("수정된 제목");
        assertThat(updated.getContent()).isEqualTo("수정된 내용");
    }

    @Test
    @DisplayName("[B-TC-010] [성공] 게시글 삭제")
    void postBoardDelete_Success() throws Exception {
        // Given: 기존 게시글 저장 및 현재 개수 기록
        Board b = new Board();
        b.setCategory("게임");
        b.setTitle("삭제 제목");
        b.setContent("삭제 내용");
        b.setAuthorName("홍길동");
        b = boardRepository.save(b);
        long before = boardRepository.count();

        // When: 삭제 요청
        mockMvc.perform(post("/board/게임/" + b.getId() + "/delete"))
                .andExpect(status().is3xxRedirection())
                .andExpect(redirectedUrl("/board/%EA%B2%8C%EC%9E%84"));

        // Then: DB에서 실제 삭제되었는지 확인
        long after = boardRepository.count();
        org.assertj.core.api.Assertions.assertThat(after).isEqualTo(before - 1);
        org.assertj.core.api.Assertions.assertThat(boardRepository.findById(b.getId())).isEmpty();
    }

    @Test
    @DisplayName("[B-TC-011] [성능] 게시글 조회 N+1 해결 확인 (Fetch Join 적용)")
    @Transactional
    void getBoardList_Performance_FetchJoin() throws Exception {
        // Given: 유저 10명과 게시글 50개 저장, 연관 설정
        Users[] users = new Users[10];
        for (int i = 0; i < users.length; i++) {
            Users u = new Users();
            u.setUserId("user" + i);
            u.setUserName("작성자" + i);
            u.setPassword("pass");
            users[i] = userRepository.save(u);
        }
        for (int i = 0; i < 50; i++) {
            Board p = new Board();
            p.setCategory("게임");
            p.setTitle("페치 테스트 " + i);
            p.setContent("내용 " + i);
            p.setCreatedAt(LocalDateTime.now());
            p.setUser(users[i % users.length]);
            boardRepository.save(p);
        }
        // 영속성 컨텍스트 초기화 (실제 쿼리 확인 목적)
        em.flush();
        em.clear();

        // When: 페치 조인 목록 조회
        List<Board> boards = boardRepository.findAllWithUserFetchJoinOrderByCreatedAtDesc();

        // Then: 연관 접근시 추가 SELECT 없이 접근 가능
        for (Board board : boards) {
            if (board.getUser() != null) {
                String name = board.getUser().getUserName();
                assertThat(name).isNotBlank();
            }
        }
    }
}

package com.SYUcap.SYUcap.Board;

import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import java.time.LocalDateTime;

import static org.junit.jupiter.api.Assertions.*;

class BoardUnitTests {

    @Test
    @DisplayName("[TC-011] 제목 길이 20자 초과 입력 시 실패")
    void titleTooLong_Fail() {
        String title = "지금 당장 도서관에서 모일 사람은 이곳으로 모이세요"; // 20자 초과
        assertTrue(title.length() > 20);
        assertFalse(BoardService.isTitleValid(title)); // 반환값: false
    }

    @Test
    @DisplayName("[TC-012] 내용 길이 200자 초과 입력 시 실패")
    void contentTooLong_Fail() {
        String content = "a".repeat(201); // 201자
        assertTrue(content.length() > 200);
        assertFalse(BoardService.isContentValid(content)); // 반환값: false
    }

    @Test
    @DisplayName("[TC-013] 시작 시간이 종료 시간보다 이후일 경우 실패 (경고 메시지)")
    void invalidMeetingTime_Fail() {
        LocalDateTime start = LocalDateTime.of(2025, 11, 23, 13, 0);
        LocalDateTime end = LocalDateTime.of(2025, 11, 23, 12, 0);
        assertTrue(start.isAfter(end));
        String message = BoardService.validateMeetingTime(start, end);
        assertEquals("시작 시간이 종료 시간보다 빠를 수 없습니다.", message);
    }

    @Test
    @DisplayName("[TC-014] 제한 인원 음수 입력 시 실패 (경고 메시지)")
    void limitCountNegative_Fail() {
        int limitCount = -5;
        assertTrue(limitCount < 1);
        String message = BoardService.validateLimitCount(limitCount);
        assertEquals("최소 1명 이상이어야 합니다", message);
    }

    @Test
    @DisplayName("[TC-015] 제한 인원 0 입력 시 실패 (경고 메시지)")
    void limitCountZero_Fail() {
        int limitCount = 0;
        assertTrue(limitCount < 1);
        String message = BoardService.validateLimitCount(limitCount);
        assertEquals("최소 1명 이상이어야 합니다", message);
    }
}

package com.SYUcap.SYUcap.Board;

import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

import java.time.LocalDateTime;

import static org.junit.jupiter.api.Assertions.*;

class BoardUnitTests {

    @Test
    @DisplayName("[TC-011] 제목 길이 20자 초과 입력 시 실패")
    void titleTooLong_Fail() {
        // Given: 20자를 초과하는 제목
        String title = "지금 당장 도서관에서 모일 사람은 이곳으로 모이세요";

        // When: 제목 유효성 검사 수행
        boolean valid = BoardService.isTitleValid(title);

        // Then: false 반환
        assertTrue(title.length() > 20);
        assertFalse(valid);
    }

    @Test
    @DisplayName("[TC-012] 내용 길이 200자 초과 입력 시 실패")
    void contentTooLong_Fail() {
        // Given: 200자를 초과하는 내용
        String content = "a".repeat(201);

        // When: 내용 유효성 검사 수행
        boolean valid = BoardService.isContentValid(content);

        // Then: false 반환
        assertTrue(content.length() > 200);
        assertFalse(valid);
    }

    @Test
    @DisplayName("[TC-013] 시작 시간이 종료 시간보다 이후일 경우 실패 (경고 메시지)")
    void invalidMeetingTime_Fail() {
        // Given: 시작 13:00, 종료 12:00
        LocalDateTime start = LocalDateTime.of(2025, 11, 23, 13, 0);
        LocalDateTime end = LocalDateTime.of(2025, 11, 23, 12, 0);

        // When: 시간 검증 수행
        String message = BoardService.validateMeetingTime(start, end);

        // Then: 경고 메시지 반환
        assertTrue(start.isAfter(end));
        assertEquals("시작 시간이 종료 시간보다 빠를 수 없습니다.", message);
    }

    @Test
    @DisplayName("[TC-014] 제한 인원 음수 입력 시 실패 (경고 메시지)")
    void limitCountNegative_Fail() {
        // Given: 제한 인원 -5
        int limitCount = -5;

        // When: 인원 검증 수행
        String message = BoardService.validateLimitCount(limitCount);

        // Then: 경고 메시지 반환
        assertTrue(limitCount < 1);
        assertEquals("최소 1명 이상이어야 합니다", message);
    }

    @Test
    @DisplayName("[TC-015] 제한 인원 0 입력 시 실패 (경고 메시지)")
    void limitCountZero_Fail() {
        // Given: 제한 인원 0
        int limitCount = 0;

        // When: 인원 검증 수행
        String message = BoardService.validateLimitCount(limitCount);

        // Then: 경고 메시지 반환
        assertTrue(limitCount < 1);
        assertEquals("최소 1명 이상이어야 합니다", message);
    }
}

package com.SYUcap.SYUcap.Comment;

import com.SYUcap.SYUcap.Board.Board;
import com.SYUcap.SYUcap.Board.BoardRepository;
import com.SYUcap.SYUcap.User.UserRepository;
import com.SYUcap.SYUcap.User.Users;
import jakarta.persistence.EntityManager;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.autoconfigure.jdbc.AutoConfigureTestDatabase;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.test.web.servlet.MockMvc;

import java.util.List;

import static org.assertj.core.api.Assertions.assertThat;
import static org.springframework.test.web.servlet.request.MockMvcRequestBuilders.post;
import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.status;

@SpringBootTest
@AutoConfigureTestDatabase
@org.springframework.boot.test.autoconfigure.web.servlet.AutoConfigureMockMvc(addFilters = false)
class CommentIntergrationTest {

    @Autowired
    private MockMvc mockMvc;

    @Autowired
    private CommentRepository commentRepository;

    @Autowired
    private BoardRepository boardRepository;

    @Autowired
    private UserRepository userRepository;

    @Autowired
    private EntityManager em;

    private Board board1;
    private Board board2;

    @BeforeEach
    void setUp() {
        // 각 테스트 실행 전에 DB 초기화
        commentRepository.deleteAll();
        boardRepository.deleteAll();

        // Given: 게시글 2건 저장
        board1 = new Board();
        board1.setCategory("게임");
        board1.setTitle("게시글 1");
        boardRepository.save(board1);

        board2 = new Board();
        board2.setCategory("스터디");
        board2.setTitle("게시글 2");
        boardRepository.save(board2);

        Comment c1 = new Comment(); c1.setBoard(board1); c1.setContent("1-1"); c1.setAuthorName("A");
        Comment c2 = new Comment(); c2.setBoard(board1); c2.setContent("1-2"); c2.setAuthorName("B");
        Comment c3 = new Comment(); c3.setBoard(board2); c3.setContent("2-1"); c3.setAuthorName("C");

        commentRepository.saveAll(List.of(c1, c2, c3));
    }

    @Test
    @DisplayName("[C-001] content 파라미터 누락 시 400, DB 저장 없음")
    void addComment_MissingContent_BadRequest() throws Exception {
        // Given: 게시글 존재, 현재 댓글 수 기록
        long before = commentRepository.count();

        // When: content 파라미터 없이 POST 요청
        mockMvc.perform(post("/board/게임/" + board1.getId() + "/comment"))
                // Then: 400 Bad Request
                .andExpect(status().isBadRequest());

        // And: DB 저장 없음
        long after = commentRepository.count();
        assertThat(after).isEqualTo(before);
    }

    @Test
    @DisplayName("[C-002] content 빈 문자열 전송 시 302 Redirect 및 DB 저장")
    void addComment_EmptyContent_RedirectAndSaved() throws Exception {
        // Given: 현재 댓글 수 기록
        long before = commentRepository.count();

        // When: content=""로 POST 요청
        mockMvc.perform(post("/board/게임/" + board1.getId() + "/comment")
                        .param("content", ""))
                // Then: 3xx Redirect (컨트롤러 리다이렉트 정책)
                .andExpect(status().is3xxRedirection());

        // And: DB에 1건 저장됨
        long after = commentRepository.count();
        assertThat(after).isEqualTo(before + 1);
    }

    @Test
    @DisplayName("[R-005] 댓글 조회 N+1 성능 검증 (JOIN FETCH)")
    void getComments_FetchJoin_NoAdditionalSelect() {
        // Given: 동일 게시글에 서로 다른 작성자(Users)로 연결된 댓글 10건 저장
        for (int i = 0; i < 10; i++) {
            Users u = new Users();
            u.setUserId("user" + i);
            u.setUserName("작성자" + i);
            u.setPassword("pass");
            u = userRepository.save(u);

            Comment c = new Comment();
            c.setBoard(board1);
            c.setContent("댓글" + i);
            c.setAuthorName(u.getUserName());
            c.setUser(u);
            commentRepository.save(c);
        }
        // 영속성 컨텍스트 초기화
        em.flush();
        em.clear();

        // When: 페치 조인으로 댓글 목록 조회
        List<Comment> comments = commentRepository.findByBoardIdWithUserFetchJoin(board1.getId());

        // Then: 각 댓글의 user.userName 접근 시 추가 SELECT 없이 접근 가능해야 함
        for (Comment c : comments) {
            if (c.getUser() != null) {
                String name = c.getUser().getUserName();
                assertThat(name).isNotBlank();
            }
        }
    }
}

